function assertHclEqual(e,t,n,r,i){(Math.abs(e.h-t)>1e-6||Math.abs(e.c-n)>1e-6||Math.abs(e.l-r)>1e-6)&&assert.fail("hcl("+e.h+", "+e.c+", "+e.l+")","hcl("+t+", "+n+", "+r+")",i||"expected {expected}, got {actual}",null,assertHclEqual)}require("../env");var vows=require("vows"),assert=require("assert"),suite=vows.describe("d3.hcl");suite.addBatch({hcl:{topic:function(){return d3.hcl},"converts string channel values to numbers":function(e){assertHclEqual(e("50","-4","32"),50,-4,32)},"converts null channel values to zero":function(e){assertHclEqual(e(null,null,null),0,0,0)},"exposes h, c and l properties":function(e){var t=e(50,-4,32);assert.equal(t.h,50),assert.equal(t.c,-4),assert.equal(t.l,32)},"changing h, c or l affects the string format":function(e){var t=e(50,-4,32);assert.equal(t+"","#444d50"),t.h++,assert.equal(t+"","#444d50"),t.c++,assert.equal(t+"","#464c4f"),t.l++,assert.equal(t+"","#494f51")},'parses hexadecimal shorthand format (e.g., "#abc")':function(e){assertHclEqual(e("#abc"),-102.28223831811077,10.774886733325554,75.10497524893663)},'parses hexadecimal format (e.g., "#abcdef")':function(e){assertHclEqual(e("#abcdef"),-100.15785184209284,20.768234621934273,81.04386565274363)},'parses HSL format (e.g., "hsl(210, 64%, 13%)")':function(e){assertHclEqual(e("hsl(210, 64.7058%, 13.33333%)"),-89.58282792342067,16.833655998102003,12.65624852526134)},'parses color names (e.g., "moccasin")':function(e){assertHclEqual(e("moccasin"),84.71288921124494,26.472460854104156,91.72317744746022)},'parses and converts RGB format (e.g., "rgb(102, 102, 0)")':function(e){assertHclEqual(e("rgb(102, 102, 0)"),102.85124420310271,49.44871600399321,41.73251953866431)},"can convert from RGB":function(e){assertHclEqual(e(d3.rgb(12,34,56)),-89.58282792342067,16.833655998102003,12.65624852526134)},"can convert from HSL":function(e){assertHclEqual(e(d3.hcl(20,.8,.3)),20,.8,.3)},"can convert to RGB":function(e){assert.rgbEqual(e("steelblue").rgb(),70,130,180)},"can derive a brighter color":function(e){assertHclEqual(e("steelblue").brighter(),-97.21873224090723,32.44906314974561,70.46551718768575),assertHclEqual(e("steelblue").brighter(.5),-97.21873224090723,32.44906314974561,61.46551718768575)},"can derive a darker color":function(e){assertHclEqual(e("lightsteelblue").darker(),-94.8160116310511,15.26488988314746,60.45157936968134),assertHclEqual(e("lightsteelblue").darker(.5),-94.8160116310511,15.26488988314746,69.45157936968134)},"string coercion returns RGB format":function(e){assert.strictEqual(e("hsl(60, 100%, 20%)")+"","#666600"),assert.strictEqual(e(d3.hcl(60,-4,32))+"","#454c51")},"roundtrip to HSL is idempotent":function(e){assert.hslEqual(d3.hsl(e("steelblue")),d3.hsl("steelblue"))},"roundtrip to RGB is idempotent":function(e){assert.hslEqual(d3.rgb(e("steelblue")),d3.rgb("steelblue"))},"roundtrip to Lab is idempotent":function(e){assert.hslEqual(d3.lab(e("steelblue")),d3.lab("steelblue"))}}}),suite.export(module)