require("../env");var vows=require("vows"),assert=require("assert"),suite=vows.describe("d3.hsl");suite.addBatch({hsl:{topic:function(){return d3.hsl},"does not clamp channel values":function(e){assert.hslEqual(e(-100,-1,-2),-100,-1,-2),assert.hslEqual(e(400,2,3),400,2,3)},"converts string channel values to numbers":function(e){assert.hslEqual(e("180",".5",".6"),180,.5,.6)},"converts null channel values to zero":function(e){assert.hslEqual(e(null,null,null),0,0,0)},"exposes h, s and l properties":function(e){var t=e("hsl(180, 50%, 60%)");assert.equal(t.h,180),assert.equal(t.s,.5),assert.equal(t.l,.6)},"changing h, s or l affects the string format":function(e){var t=e("hsl(180, 50%, 60%)");t.h++,t.s+=.1,t.l+=.1,assert.equal(t+"","#85dfe0")},'parses hexadecimal shorthand format (e.g., "#abc")':function(e){assert.hslEqual(e("#abc"),210,.25,.733333)},'parses hexadecimal format (e.g., "#abcdef")':function(e){assert.hslEqual(e("#abcdef"),210,.68,.803922)},'parses HSL format (e.g., "hsl(210, 64%, 13%)")':function(e){assert.hslEqual(e("hsl(210, 64.7058%, 13.33333%)"),210,.647058,.133333)},'parses color names (e.g., "moccasin")':function(e){assert.hslEqual(e("moccasin"),38.108108,1,.854902),assert.hslEqual(e("aliceblue"),208,1,.970588),assert.hslEqual(e("yellow"),60,1,.5)},'parses and converts RGB format (e.g., "rgb(102, 102, 0)")':function(e){assert.hslEqual(e("rgb(102, 102, 0)"),60,1,.2)},"can convert from RGB":function(e){assert.hslEqual(e(d3.rgb(12,34,56)),210,.647058,.133333)},"can convert from HSL":function(e){assert.hslEqual(e(d3.hsl(20,.8,.3)),20,.8,.3)},"can convert to RGB":function(e){assert.rgbEqual(e("steelblue").rgb(),70,130,180)},"can derive a brighter color":function(e){assert.hslEqual(e("steelblue").brighter(),207.272727,.44,.7002801),assert.hslEqual(e("steelblue").brighter(.5),207.272727,.44,.5858964),assert.hslEqual(e("steelblue").brighter(1),207.272727,.44,.7002801),assert.hslEqual(e("steelblue").brighter(2),207.272727,.44,1.0004002)},"can derive a darker color":function(e){assert.hslEqual(e("lightsteelblue").darker(),213.913043,.4107143,.5462745),assert.hslEqual(e("lightsteelblue").darker(.5),213.913043,.4107143,.6529229),assert.hslEqual(e("lightsteelblue").darker(1),213.913043,.4107143,.5462745),assert.hslEqual(e("lightsteelblue").darker(2),213.913043,.4107143,.38239216)},"string coercion returns RGB format":function(e){assert.strictEqual(e("hsl(60, 100%, 20%)")+"","#666600"),assert.strictEqual(e(d3.hsl(60,1,.2))+"","#666600")}}}),suite.export(module)