require("../env");var vows=require("vows"),assert=require("assert"),suite=vows.describe("d3.min");suite.addBatch({min:{topic:function(){return d3.min},"returns the least numeric value for numbers":function(e){assert.equal(e([1]),1),assert.equal(e([5,1,2,3,4]),1),assert.equal(e([20,3]),3),assert.equal(e([3,20]),3)},"returns the least lexicographic value for strings":function(e){assert.equal(e(["c","a","b"]),"a"),assert.equal(e(["20","3"]),"20"),assert.equal(e(["3","20"]),"20")},"ignores null, undefined and NaN":function(e){assert.equal(e([NaN,1,2,3,4,5]),1),assert.equal(e([1,2,3,4,5,NaN]),1),assert.equal(e([10,null,3,undefined,5,NaN]),3)},"compares heterogenous types as numbers":function(e){assert.strictEqual(e([20,"3"]),"3"),assert.strictEqual(e(["20",3]),3),assert.strictEqual(e([3,"20"]),3),assert.strictEqual(e(["3",20]),"3")},"returns undefined for empty array":function(e){assert.isUndefined(e([])),assert.isUndefined(e([null])),assert.isUndefined(e([undefined])),assert.isUndefined(e([NaN])),assert.isUndefined(e([NaN,NaN]))},"applies the optional accessor function":function(e){assert.equal(d3.min([[1,2,3,4,5],[2,4,6,8,10]],function(e){return d3.max(e)}),5),assert.equal(d3.min([1,2,3,4,5],function(e,t){return t}),0)}}}),suite.export(module)