require("../env");var vows=require("vows"),assert=require("assert"),suite=vows.describe("d3.round");suite.addBatch({round:{topic:function(){return d3.round},"returns a number":function(e){assert.isNumber(e(42))},"returns zero for zero":function(e){assert.equal(e(0),0)},"ignores degenerate input":function(e){assert.isNaN(e(NaN)),assert.equal(e(Infinity),Infinity),assert.equal(e(-Infinity),-Infinity)},"returns integers by default":function(e){assert.equal(e(10.6),11),assert.equal(e(10.4),10),assert.equal(e(.6),1),assert.equal(e(.4),0),assert.equal(e(-0.6),-1),assert.equal(e(-0.4),0),assert.equal(e(-10.6),-11),assert.equal(e(-10.4),-10)},"rounds to the specified decimal place":function(e){assert.inDelta(e(10.56,1),10.6,1e-6),assert.inDelta(e(10.54,1),10.5,1e-6),assert.inDelta(e(.56,1),.6,1e-6),assert.inDelta(e(.54,1),.5,1e-6),assert.inDelta(e(-0.56,1),-0.6,1e-6),assert.inDelta(e(-0.54,1),-0.5,1e-6),assert.inDelta(e(-10.56,1),-10.6,1e-6),assert.inDelta(e(-10.54,1),-10.5,1e-6),assert.inDelta(e(10.556,2),10.56,1e-6),assert.inDelta(e(10.554,2),10.55,1e-6),assert.inDelta(e(.556,2),.56,1e-6),assert.inDelta(e(.554,2),.55,1e-6),assert.inDelta(e(-0.556,2),-0.56,1e-6),assert.inDelta(e(-0.554,2),-0.55,1e-6),assert.inDelta(e(-10.556,2),-10.56,1e-6),assert.inDelta(e(-10.554,2),-10.55,1e-6)},"rounds to the specified significant digits":function(e){assert.equal(e(123.45,-1),120),assert.equal(e(345.67,-1),350),assert.equal(e(-123.45,-1),-120),assert.equal(e(-345.67,-1),-350),assert.equal(e(123.45,-2),100),assert.equal(e(456.78,-2),500),assert.equal(e(-123.45,-2),-100),assert.equal(e(-456.78,-2),-500),assert.equal(e(123.45,-3),0),assert.equal(e(567.89,-3),1e3),assert.equal(e(-123.45,-3),0),assert.equal(e(-567.89,-3),-1e3)}}}),suite.export(module)