require("../env");var vows=require("vows"),assert=require("assert"),suite=vows.describe("selection.empty");suite.addBatch({"select(body)":{topic:function(){return d3.select("body").html("")},"returns true for empty selections":function(e){assert.isTrue(e.select("foo").empty())},"returns false for non-empty selections":function(e){assert.isFalse(e.empty())},"ignores null nodes":function(e){var t=d3.select("body");t[0][0]=null,assert.isTrue(t.empty())}}}),suite.addBatch({"selectAll(div)":{topic:function(){var e=d3.select("body").html("");return e.append("div").append("span"),e.append("div"),e.selectAll("div")},"returns true for empty selections":function(e){assert.isTrue(e.select("foo").empty())},"returns false for non-empty selections":function(e){assert.isFalse(e.empty()),assert.isFalse(e.select("span").empty())},"ignores null nodes":function(e){var t=d3.selectAll("div");t[0][0]=null,assert.isTrue(t.select("span").empty())}}}),suite.export(module)