require("../env");var vows=require("vows"),assert=require("assert"),suite=vows.describe("selection.node");suite.addBatch({"select(body)":{topic:function(){return d3.select("body").html("")},"returns null for empty selections":function(e){assert.isNull(e.select("foo").node())},"returns the first element for non-empty selections":function(e){assert.isTrue(e.node()===document.body)},"ignores null nodes":function(e){var t=d3.select("body");t[0][0]=null,assert.isNull(t.node())}}}),suite.addBatch({"selectAll(div)":{topic:function(){var e=d3.select("body").html("");return e.append("div").append("span"),e.append("div"),e.selectAll("div")},"returns null for empty selections":function(e){assert.isNull(e.select("foo").node())},"returns the first element for non-empty selections":function(e){assert.isTrue(e.node()===e[0][0])},"ignores null nodes":function(e){var t=d3.selectAll("div");t[0][0]=null,assert.isTrue(t.node()===e[0][1])}}}),suite.export(module)