require("../env");var vows=require("vows"),assert=require("assert"),suite=vows.describe("d3.selection");suite.addBatch({selection:{topic:function(){return d3.selection()},"selects the document":function(e){assert.equal(e.length,1),assert.equal(e[0].length,1),assert.equal(e[0][0],document)},"is an instanceof d3.selection":function(e){assert.instanceOf(e,d3.selection)},"subselections are also instanceof d3.selection":function(e){assert.instanceOf(e.select("body"),d3.selection),assert.instanceOf(e.selectAll("body"),d3.selection)},"selection prototype can be extended":function(e){d3.selection.prototype.foo=function(e){return this.attr("foo",e)},e.select("body").foo(42),assert.equal(document.body.getAttribute("foo"),"42"),delete d3.selection.prototype.foo}}}),suite.export(module)