require("../env");var assert=require("assert");module.exports={topic:function(){return d3.select("body").append("div").transition()},"is approximately equal to now":function(e){var t=e.time;assert.inDelta(t,Date.now(),20)},"increases monotonically across transitions":function(e){var t=Date.now,n=Date.now();try{Date.now=function(){return++n};var r=d3.select("body").append("div").transition(),i=d3.select("body").append("div").transition();assert.isTrue(i.time>r.time)}finally{Date.now=t}},"is inherited by subtransitions":function(e){var t=Date.now,n=Date.now();try{Date.now=function(){return++n};var r=d3.select("body").append("div").transition(),i=r.transition();assert.equal(i.time,r.time)}finally{Date.now=t}}}