require("../env");var vows=require("vows"),assert=require("assert"),suite=vows.describe("d3.tween");suite.addBatch({tween:{"coerces constants to strings before interpolating":function(){var e=d3.tween({toString:function(){return"#fff"}},d3.interpolate),t=e(null,0,"red");assert.strictEqual(t(0),"#ff0000"),assert.strictEqual(t(.5),"#ff8080"),assert.strictEqual(t(1),"#ffffff")},"coerces function return values to strings before interpolating":function(){var e=d3.tween(function(e){return{toString:function(){return e}}},d3.interpolate),t=e("#fff",0,"red");assert.strictEqual(t(0),"#ff0000"),assert.strictEqual(t(.5),"#ff8080"),assert.strictEqual(t(1),"#ffffff")}}}),suite.export(module)