require("../env");var vows=require("vows"),assert=require("assert"),suite=vows.describe("d3.geo.mercator");suite.addBatch({mercator:{topic:function(){return d3.geo.mercator().translate([0,0]).scale(100)},Arctic:function(e){var t=e([0,85]);assert.inDelta(t[0],0,1e-6),assert.inDelta(t[1],-49.8362085,1e-6);var n=e.invert(t);assert.inDelta(n[0],0,1e-6),assert.inDelta(n[1],85,1e-6)},Antarctic:function(e){var t=e([0,-85]);assert.inDelta(t[0],0,1e-6),assert.inDelta(t[1],49.8362085,1e-6);var n=e.invert(t);assert.inDelta(n[0],0,1e-6),assert.inDelta(n[1],-85,1e-6)},Hawaii:function(e){var t=e([-180,0]);assert.inDelta(t[0],-50,1e-6),assert.inDelta(t[1],0,1e-6);var n=e.invert(t);assert.inDelta(n[0],-180,1e-6),assert.inDelta(n[1],0,1e-6)},Phillipines:function(e){var t=e([180,0]);assert.inDelta(t[0],50,1e-6),assert.inDelta(t[1],0,1e-6);var n=e.invert(t);assert.inDelta(n[0],180,1e-6),assert.inDelta(n[1],0,1e-6)},"Inversion works for non-zero translation":function(){var e=d3.geo.mercator().translate([123,99]).scale(100),t=e([0,85]),n=e.invert(t);assert.inDelta(n[0],0,1e-6),assert.inDelta(n[1],85,1e-6)}}}),suite.export(module)