function category(e,t){return{"is an ordinal scale":function(){var r=e(),i=r.range();assert.lengthOf(r.domain(),0),assert.lengthOf(r.range(),t),assert.equal(r(1),i[0]),assert.equal(r(2),i[1]),assert.equal(r(1),i[0]);var s=r.copy();assert.deepEqual(s.domain(),r.domain()),assert.deepEqual(s.range(),r.range()),r.domain(d3.range(t));for(var o=0;o<t;++o)assert.equal(r(o+t),r(o));assert.equal(s(1),i[0]),assert.equal(s(2),i[1])},"each instance is isolated":function(){var t=e(),n=e(),r=t.range();assert.equal(t(1),r[0]),assert.equal(n(2),r[0]),assert.equal(n(1),r[1]),assert.equal(t(1),r[0])},"contains the expected number of values in the range":function(){var r=e();assert.lengthOf(r.range(),t)},"each range value is distinct":function(){var t={},n=0,r=e();r.range().forEach(function(e){e in t||(t[e]=++n)}),assert.equal(n,r.range().length)},"each range value is a hexadecimal color":function(){var t=e();t.range().forEach(function(e){assert.match(e,/#[0-9a-f]{6}/),e=d3.rgb(e),assert.isFalse(isNaN(e.r)),assert.isFalse(isNaN(e.g)),assert.isFalse(isNaN(e.b))})},"no range values are very dark or very light":function(){var t=e();t.range().forEach(function(e){var t=d3.hsl(e);assert.isTrue(t.l>=.34,"expected "+e+" to be lighter (l = "+t.l+")"),assert.isTrue(t.l<=.89,"expected "+e+" to be darker (l = "+t.l+")")})}}}require("../env");var vows=require("vows"),assert=require("assert"),suite=vows.describe("d3.scale.category");suite.addBatch({category10:category(d3.scale.category10,10),category20:category(d3.scale.category20,20),category20b:category(d3.scale.category20b,20),category20c:category(d3.scale.category20c,20)}),suite.export(module)