require("../env");var vows=require("vows"),assert=require("assert"),time=require("./time"),local=time.local,utc=time.utc,suite=vows.describe("d3.time.day");suite.addBatch({day:{topic:function(){return d3.time.day},"defaults to floor":function(e){assert.strictEqual(e,e.floor)},floor:{topic:function(e){return e.floor},"returns midnights":function(e){assert.deepEqual(e(local(2010,11,31,23)),local(2010,11,31)),assert.deepEqual(e(local(2011,0,1,0)),local(2011,0,1)),assert.deepEqual(e(local(2011,0,1,1)),local(2011,0,1))},"observes start of daylight savings time":function(e){assert.deepEqual(e(utc(2011,2,13,7)),local(2011,2,12)),assert.deepEqual(e(utc(2011,2,13,8)),local(2011,2,13)),assert.deepEqual(e(utc(2011,2,13,9)),local(2011,2,13)),assert.deepEqual(e(utc(2011,2,13,10)),local(2011,2,13))},"observes end of daylight savings time":function(e){assert.deepEqual(e(utc(2011,10,6,7)),local(2011,10,6)),assert.deepEqual(e(utc(2011,10,6,8)),local(2011,10,6)),assert.deepEqual(e(utc(2011,10,6,9)),local(2011,10,6)),assert.deepEqual(e(utc(2011,10,6,10)),local(2011,10,6))},"correctly handles years in the first century":function(e){assert.deepEqual(e(local(9,10,6,7)),local(9,10,6))}},ceil:{topic:function(e){return e.ceil},"returns midnights":function(e){assert.deepEqual(e(local(2010,11,30,23)),local(2010,11,31)),assert.deepEqual(e(local(2010,11,31,0)),local(2010,11,31)),assert.deepEqual(e(local(2010,11,31,1)),local(2011,0,1))},"observes start of daylight savings time":function(e){assert.deepEqual(e(utc(2011,2,13,7)),local(2011,2,13)),assert.deepEqual(e(utc(2011,2,13,8)),local(2011,2,13)),assert.deepEqual(e(utc(2011,2,13,9)),local(2011,2,14)),assert.deepEqual(e(utc(2011,2,13,10)),local(2011,2,14))},"observes end of daylight savings time":function(e){assert.deepEqual(e(utc(2011,10,6,7)),local(2011,10,6)),assert.deepEqual(e(utc(2011,10,6,8)),local(2011,10,7)),assert.deepEqual(e(utc(2011,10,6,9)),local(2011,10,7)),assert.deepEqual(e(utc(2011,10,6,10)),local(2011,10,7))},"handles midnight for leap years":function(e){assert.deepEqual(e(utc(2012,2,1,0)),local(2012,2,1)),assert.deepEqual(e(utc(2012,2,1,0)),local(2012,2,1))}},offset:{topic:function(e){return e.offset},"does not modify the passed-in date":function(e){var t=local(2010,11,31,23,59,59,999);e(t,1),assert.deepEqual(t,local(2010,11,31,23,59,59,999))},"does not round the passed-in-date":function(e){assert.deepEqual(e(local(2010,11,31,23,59,59,999),1),local(2011,0,1,23,59,59,999)),assert.deepEqual(e(local(2010,11,31,23,59,59,456),-2),local(2010,11,29,23,59,59,456))},"allows negative offsets":function(e){assert.deepEqual(e(local(2010,11,31),-1),local(2010,11,30)),assert.deepEqual(e(local(2011,0,1),-2),local(2010,11,30)),assert.deepEqual(e(local(2011,0,1),-1),local(2010,11,31))},"allows positive offsets":function(e){assert.deepEqual(e(local(2010,11,31),1),local(2011,0,1)),assert.deepEqual(e(local(2010,11,30),2),local(2011,0,1)),assert.deepEqual(e(local(2010,11,30),1),local(2010,11,31))},"allows zero offset":function(e){assert.deepEqual(e(local(2010,11,31,23,59,59,999),0),local(2010,11,31,23,59,59,999)),assert.deepEqual(e(local(2010,11,31,23,59,58,0),0),local(2010,11,31,23,59,58,0))}},UTC:{topic:function(e){return e.utc},"defaults to floor":function(e){assert.strictEqual(e,e.floor)},floor:{topic:function(e){return e.floor},"returns midnights":function(e){assert.deepEqual(e(utc(2010,11,31,23)),utc(2010,11,31)),assert.deepEqual(e(utc(2011,0,1,0)),utc(2011,0,1)),assert.deepEqual(e(utc(2011,0,1,1)),utc(2011,0,1))},"does not observe the start of daylight savings time":function(e){assert.deepEqual(e(utc(2011,2,13,7)),utc(2011,2,13)),assert.deepEqual(e(utc(2011,2,13,8)),utc(2011,2,13)),assert.deepEqual(e(utc(2011,2,13,9)),utc(2011,2,13)),assert.deepEqual(e(utc(2011,2,13,10)),utc(2011,2,13))},"does not observe the end of daylight savings time":function(e){assert.deepEqual(e(utc(2011,10,6,5)),utc(2011,10,6)),assert.deepEqual(e(utc(2011,10,6,6)),utc(2011,10,6)),assert.deepEqual(e(utc(2011,10,6,7)),utc(2011,10,6)),assert.deepEqual(e(utc(2011,10,6,8)),utc(2011,10,6))}},ceil:{topic:function(e){return e.ceil},"returns midnights":function(e){assert.deepEqual(e(utc(2010,11,30,23)),utc(2010,11,31)),assert.deepEqual(e(utc(2010,11,31,0)),utc(2010,11,31)),assert.deepEqual(e(utc(2010,11,31,1)),utc(2011,0,1))},"does not observe the start of daylight savings time":function(e){assert.deepEqual(e(utc(2011,2,13,7)),utc(2011,2,14)),assert.deepEqual(e(utc(2011,2,13,8)),utc(2011,2,14)),assert.deepEqual(e(utc(2011,2,13,9)),utc(2011,2,14)),assert.deepEqual(e(utc(2011,2,13,10)),utc(2011,2,14))},"does not observe the end of daylight savings time":function(e){assert.deepEqual(e(utc(2011,10,6,5)),utc(2011,10,7)),assert.deepEqual(e(utc(2011,10,6,6)),utc(2011,10,7)),assert.deepEqual(e(utc(2011,10,6,7)),utc(2011,10,7)),assert.deepEqual(e(utc(2011,10,6,8)),utc(2011,10,7))}},offset:{topic:function(e){return e.offset},"does not modify the passed-in date":function(e){var t=utc(2010,11,31,23,59,59,999);e(t,1),assert.deepEqual(t,utc(2010,11,31,23,59,59,999))},"does not round the passed-in-date":function(e){assert.deepEqual(e(utc(2010,11,31,23,59,59,999),1),utc(2011,0,1,23,59,59,999)),assert.deepEqual(e(utc(2010,11,31,23,59,59,456),-2),utc(2010,11,29,23,59,59,456))},"allows negative offsets":function(e){assert.deepEqual(e(utc(2010,11,31),-1),utc(2010,11,30)),assert.deepEqual(e(utc(2011,0,1),-2),utc(2010,11,30)),assert.deepEqual(e(utc(2011,0,1),-1),utc(2010,11,31))},"allows positive offsets":function(e){assert.deepEqual(e(utc(2010,11,31),1),utc(2011,0,1)),assert.deepEqual(e(utc(2010,11,30),2),utc(2011,0,1)),assert.deepEqual(e(utc(2010,11,30),1),utc(2010,11,31))},"allows zero offset":function(e){assert.deepEqual(e(utc(2010,11,31,23,59,59,999),0),utc(2010,11,31,23,59,59,999)),assert.deepEqual(e(utc(2010,11,31,23,59,58,0),0),utc(2010,11,31,23,59,58,0))}}}}}),suite.export(module)